		  (40.6597 -112.391179245,40.5745137316 -112.355144716,40.5218657223 -112.260805093,40.6597 -112.391179245),
		  (40.5218657223 -112.144194907,40.5745137316 -112.049855284,40.6597 -112.013820755,40.5218657223 -112.144194907)
		)
		*/
		$binary = $this->hex2bin("");
		$obj = WkbParser::parse($binary);

		echo $obj->toWKT()."\n";
/*
binCoverage: 
textCoverage: POLYGON((40.6597 -112.013820755,40.7448862684 -112.049855284,40.7975342777 -112.144194907,40.7975342777 -112.260805093,40.7448862684 -112.355144716,40.6597 -112.391179245,40.5745137316 -112.355144716,40.5218657223 -112.260805093,40.5218657223 -112.144194907,40.5745137316 -112.049855284,40.6597 -112.013820755))
